import React from 'react';

export default function Portfolio() {
  const portfolioItems = [
    {
      id: 1,
      title: "Geometric Branding",
      category: "BRAND IDENTITY",
      size: "large"
    },
    {
      id: 2,
      title: "Modern Logo Design",
      category: "LOGO DESIGN",
      size: "small"
    },
    {
      id: 3,
      title: "Brand Collateral",
      category: "PRINT DESIGN",
      size: "small"
    },
    {
      id: 4,
      title: "Packaging Design",
      category: "PACKAGING",
      size: "large"
    }
  ];

  return (
    <section className="py-24 px-6 min-h-screen bg-[#0d0d0d]">
      <div className="max-w-7xl mx-auto">
        <p className="text-[#EC7B2F] text-sm tracking-[0.3em] mb-4 text-center">
          OUR WORK
        </p>
        <h2 className="text-5xl font-bold text-center mb-16">PORTFOLIO</h2>

        <div className="grid grid-cols-2 gap-4">
          {portfolioItems.map((item) => (
            <div
              key={item.id}
              className={`relative group overflow-hidden rounded-lg cursor-pointer ${
                item.size === 'large' ? 'col-span-2' : 'col-span-1'
              }`}
              style={{ aspectRatio: item.size === 'large' ? '16/9' : '1/1' }}
            >
              <div className="absolute inset-0 bg-gradient-to-br from-[#EC7B2F] to-[#d66b25] opacity-80"></div>

              <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-all duration-500 transform group-hover:scale-110">
                <div className="text-center">
                  <p className="text-xs tracking-widest mb-2 text-white/80">
                    {item.category}
                  </p>
                  <h3 className="text-2xl font-bold text-white">
                    {item.title}
                  </h3>
                </div>
              </div>

              <div className="absolute inset-0 bg-black/50 group-hover:bg-black/0 transition-all duration-500"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
