import { ArrowRight } from 'lucide-react';

const Hero = () => {
  return (
    <section className="relative min-h-screen flex flex-col justify-center bg-gradient-dark overflow-hidden pt-16 pb-24">
      {/* Decorative subtle squares - matching the faint orange outlines in the image */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-32 left-12 w-40 h-40 border border-primary/10 rotate-12" />
        <div className="absolute bottom-32 right-12 w-48 h-48 border border-primary/10 -rotate-12" />
      </div>

      <div className="container mx-auto px-6 max-w-7xl relative z-10">
        <div className="text-center">

          {/* "CREATIVE DESIGN STUDIO" */}
          <p className="text-primary uppercase tracking-[0.35em] text-sm mb-12 opacity-90 font-normal">
            Creative Design Studio
          </p>

          {/* Main headline - stacked with intentional overlap */}
          <div className="space-y-[-1.5rem] md:space-y-[-2.5rem] lg:space-y-[-3.5rem]">
            <h1 className="font-display text-8xl md:text-9xl lg:text-10xl leading-none text-foreground font-normal tracking-tight">
              WE CREATE
            </h1>
            <h1 className="font-display text-9xl md:text-10xl lg:text-11xl leading-[0.9] text-foreground font-normal tracking-tight">
              <span className="text-gradient font-normal">BOLD</span> BRANDS
            </h1>
          </div>

          {/* Description paragraph */}
          <p className="mt-12 md:mt-14 max-w-2xl mx-auto text-muted-foreground text-lg leading-relaxed">
            Award-winning graphic design agency crafting memorable visual identities 
            that make brands stand out in a crowded world.
          </p>

          {/* Buttons */}
          <div className="mt-12 flex flex-col sm:flex-row gap-6 justify-center items-center">
            {/* Primary button - orange with rounded corners, arrow */}
            <a
              href="#portfolio"
              className="group inline-flex items-center justify-center px-10 py-4 bg-primary text-white rounded-md text-sm uppercase tracking-wider font-medium shadow-orange hover:bg-primary/90 transition-all"
            >
              View Our Work
              <ArrowRight className="ml-3 w-5 h-5 group-hover:translate-x-2 transition-transform duration-300" />
            </a>

            {/* Secondary button - outlined, no fill, white border */}
            <a
              href="#contact"
              className="inline-flex items-center justify-center px-10 py-4 border-2 border-white/80 text-foreground rounded-md text-sm uppercase tracking-wider font-medium hover:bg-white/10 transition-all"
            >
              Get In Touch
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
