const Hero = () => {
  return (
    <section className="min-h-screen flex items-center justify-center bg-gradient-dark pt-20">
      <div className="container mx-auto px-6 max-w-7xl">
        <div className="max-w-5xl mx-auto text-center">
          <p 
            className="font-body text-primary uppercase tracking-[0.3em] mb-6 text-sm" // Added text-sm
            style={{ animationDelay: '0.1s' }}
          >
            Creative Design Studio
          </p>
          
          <h1 
            className="font-display text-7xl md:text-8xl lg:text-9xl text-foreground leading-none mb-8" // Changed from text-6xl to text-7xl
            style={{ animationDelay: '0.2s' }}
          >
            WE CREATE
            <br />
            <span className="text-gradient">BOLD</span> BRANDS
          </h1>
          
          <p 
            className="font-body text-muted-foreground text-base md:text-lg max-w-2xl mx-auto mb-12" // Changed from text-lg md:text-xl to text-base md:text-lg
            style={{ animationDelay: '0.3s' }}
          >
            Award-winning graphic design agency crafting memorable visual identities 
            that make brands stand out in a crowded world.
          </p>
          
          <div 
            className="flex flex-col sm:flex-row gap-4 justify-center" 
            style={{ animationDelay: '0.4s' }}
          >
            <a
              href="#portfolio"
              className="inline-flex items-center justify-center px-8 py-4 bg-primary text-white font-body font-medium rounded-lg hover:bg-primary/90 transition-all shadow-orange text-sm uppercase tracking-wider" // Added text-sm uppercase tracking-wider
            >
              View Our Work
              <ArrowRight className="ml-2 w-4 h-4" /> {/* Changed from w-5 h-5 to w-4 h-4 */}
            </a>
            <a
              href="#contact"
              className="inline-flex items-center justify-center px-8 py-4 bg-transparent border-2 border-primary text-primary font-body font-medium rounded-lg hover:bg-primary hover:text-white transition-all text-sm uppercase tracking-wider" // Added text-sm uppercase tracking-wider
            >
              Get In Touch
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};
