import React from 'react';
import { Palette, Layout, Pen, Monitor, Package, Camera } from 'lucide-react';

export default function Services() {
  const services = [
    {
      icon: <Palette className="w-8 h-8" />,
      title: "Brand Identity",
      description: "Complete visual identity systems that capture your brand's essence and resonate with your audience."
    },
    {
      icon: <Layout className="w-8 h-8" />,
      title: "UI/UX Design",
      description: "Intuitive digital experiences that delight users and drive business results."
    },
    {
      icon: <Pen className="w-8 h-8" />,
      title: "Logo Design",
      description: "Distinctive logos that become the cornerstone of your brand recognition."
    },
    {
      icon: <Monitor className="w-8 h-8" />,
      title: "Web Design",
      description: "Stunning websites that combine aesthetics with seamless functionality."
    },
    {
      icon: <Package className="w-8 h-8" />,
      title: "Packaging",
      description: "Eye-catching packaging designs that stand out on shelves and online."
    },
    {
      icon: <Camera className="w-8 h-8" />,
      title: "Art Direction",
      description: "Strategic creative direction for campaigns that capture attention."
    }
  ];

  return (
    <section className="py-24 px-6 min-h-screen">
      <div className="max-w-7xl mx-auto">
        <p className="text-[#EC7B2F] text-sm tracking-[0.3em] mb-4 text-center">
          WHAT WE DO
        </p>
        <h2 className="text-5xl font-bold text-center mb-16">OUR SERVICES</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-[#1a1a1a] p-8 rounded-lg border border-gray-800 transition-all duration-300 hover:border-[#EC7B2F] hover:shadow-xl hover:shadow-[#EC7B2F]/20 hover:-translate-y-1 group"
            >
              <div className="bg-[#2a2a2a] w-16 h-16 rounded flex items-center justify-center mb-6 text-[#EC7B2F] group-hover:bg-[#EC7B2F] group-hover:text-white transition-all duration-300">
                {service.icon}
              </div>
              <h3 className="text-2xl font-bold mb-4">{service.title}</h3>
              <p className="text-gray-400">{service.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
