import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send } from 'lucide-react';

export default function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        alert('Message sent successfully!');
        setFormData({ name: '', email: '', message: '' });
      } else {
        alert('Failed to send message. Please try again.');
      }
    } catch (error) {
      alert('Error sending message. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <section className="py-24 px-6 min-h-screen">
      <div className="max-w-7xl mx-auto">
        <p className="text-[#EC7B2F] text-sm tracking-[0.3em] mb-4 text-center">
          GET IN TOUCH
        </p>
        <h2 className="text-5xl font-bold text-center mb-16">CONTACT US</h2>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
          <div>
            <h3 className="text-3xl font-bold mb-6">LET'S CREATE SOMETHING BOLD</h3>
            <p className="text-gray-400 mb-12">
              Ready to transform your brand? We'd love to hear about your project. Drop 
              us a line and let's start a conversation.
            </p>

            <div className="space-y-6">
              <div className="flex items-start gap-4">
                <div className="w-12 h-12 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                  <Mail size={20} />
                </div>
                <div>
                  <div className="text-sm text-gray-400 mb-1">EMAIL</div>
                  <div className="text-lg">hello@colorstencil.com</div>
                </div>
              </div>

              <div className="flex items-start gap-4">
                <div className="w-12 h-12 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                  <Phone size={20} />
                </div>
                <div>
                  <div className="text-sm text-gray-400 mb-1">PHONE</div>
                  <div className="text-lg">+1 (555) 123-4567</div>
                </div>
              </div>

              <div className="flex items-start gap-4">
                <div className="w-12 h-12 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                  <MapPin size={20} />
                </div>
                <div>
                  <div className="text-sm text-gray-400 mb-1">LOCATION</div>
                  <div className="text-lg">New York, NY</div>
                </div>
              </div>
            </div>
          </div>

          <div>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm text-gray-400 mb-2">NAME</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  placeholder="Your name"
                  className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-4 py-3 focus:border-[#EC7B2F] focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="block text-sm text-gray-400 mb-2">EMAIL</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  placeholder="your@email.com"
                  className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-4 py-3 focus:border-[#EC7B2F] focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="block text-sm text-gray-400 mb-2">MESSAGE</label>
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  required
                  placeholder="Tell us about your project..."
                  rows="6"
                  className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-4 py-3 focus:border-[#EC7B2F] focus:outline-none transition-colors resize-none"
                ></textarea>
              </div>

              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full bg-[#EC7B2F] px-8 py-4 rounded flex items-center justify-center gap-2 hover:bg-[#d66b25] transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? 'SENDING...' : 'SEND MESSAGE'} <Send size={18} />
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
