// src/components/Navbar.jsx
import React from 'react';

export default function Navbar() {
  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      const offset = 80; // Navbar height
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - offset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-[#0d0d0d]/95 backdrop-blur-sm border-b border-gray-800">
      <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">
        <div className="flex items-center">
          <img 
            src="https://i.postimg.cc/mDX5LR8S/logo.png" 
            alt="Color Stencil" 
            className="h-10 w-auto cursor-pointer"
            onClick={() => scrollToSection('home')}
          />
        </div>
        <div className="flex gap-1">
          <button 
            onClick={() => scrollToSection('home')} 
            className="px-3 py-1.5 text-sm font-light rounded transition-all duration-300 hover:bg-[#EC7B2F]"
          >
            HOME
          </button>
          <button 
            onClick={() => scrollToSection('services')} 
            className="px-3 py-1.5 text-sm font-light rounded transition-all duration-300 hover:bg-[#EC7B2F]"
          >
            SERVICES
          </button>
          <button 
            onClick={() => scrollToSection('portfolio')} 
            className="px-3 py-1.5 text-sm font-light rounded transition-all duration-300 hover:bg-[#EC7B2F]"
          >
            PORTFOLIO
          </button>
          <button 
            onClick={() => scrollToSection('about')} 
            className="px-3 py-1.5 text-sm font-light rounded transition-all duration-300 hover:bg-[#EC7B2F]"
          >
            ABOUT
          </button>
          <button 
            onClick={() => scrollToSection('contact')} 
            className="px-3 py-1.5 text-sm font-light rounded transition-all duration-300 hover:bg-[#EC7B2F]"
          >
            CONTACT
          </button>
        </div>
      </div>
    </nav>
  );
}
