import { ArrowRight } from 'lucide-react';

const Hero = () => {
  return (
    <section className="min-h-screen flex flex-col justify-center bg-gradient-dark relative overflow-hidden">
      {/* Subtle decorative squares (visible in the reference image) */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-20 left-10 w-32 h-32 border border-primary/20 rotate-12" />
        <div className="absolute bottom-20 right-10 w-40 h-40 border border-primary/20 -rotate-12" />
      </div>

      <div className="container mx-auto px-6 max-w-7xl relative z-10">
        <div className="text-center">
          {/* "Creative Design Studio" */}
          <p className="text-primary uppercase tracking-[0.4em] text-xs md:text-sm font-light mb-8 opacity-90">
            Creative Design Studio
          </p>

          {/* Main headline */}
          <div className="space-y-[-2rem] md:space-y-[-3rem] lg:space-y-[-4rem]">
            <h1 className="font-display text-7xl sm:text-8xl md:text-9xl lg:text-[10rem] leading-[0.85] text-white font-thin">
              WE CREATE
            </h1>
            <h1 className="font-display text-8xl sm:text-9xl md:text-10xl lg:text-[11.5rem] leading-[0.85] text-white">
              <span className="text-gradient font-semibold">BOLD</span> BRANDS
            </h1>
          </div>

          {/* Description paragraph */}
          <p className="mt-12 md:mt-16 max-w-3xl mx-auto text-muted-foreground text-base md:text-lg leading-relaxed px-4">
            Award-winning graphic design agency crafting memorable visual identities<br />
            that make brands stand out in a crowded world.
          </p>

          {/* Buttons */}
          <div className="mt-12 md:mt-16 flex flex-col sm:flex-row gap-6 justify-center items-center">
            <a
              href="#portfolio"
              className="group inline-flex items-center justify-center px-10 py-5 bg-primary text-white rounded-full text-sm uppercase tracking-wider font-medium shadow-lg shadow-orange-500/30 hover:shadow-orange-500/50 transition-all duration-300"
            >
              View Our Work
              <ArrowRight className="ml-3 w-5 h-5 group-hover:translate-x-1 transition-transform" />
            </a>

            <a
              href="#contact"
              className="inline-flex items-center justify-center px-10 py-5 border-2 border-white/80 text-white rounded-full text-sm uppercase tracking-wider font-medium hover:bg-white hover:text-black transition-all duration-300"
            >
              Get In Touch
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
