// src/pages/Home.jsx
import React, { useState } from 'react';
import { Palette, Layout, Pen, Monitor, Package, Camera, ChevronLeft, ChevronRight, Mail, Phone, MapPin, Send } from 'lucide-react';

export default function Home() {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [selectedImage, setSelectedImage] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const services = [
    {
      icon: <Palette className="w-6 h-6" />,
      title: "Brand Identity",
      description: "Complete visual identity systems that capture your brand's essence and resonate with your audience."
    },
    {
      icon: <Layout className="w-6 h-6" />,
      title: "UI/UX Design",
      description: "Intuitive digital experiences that delight users and drive business results."
    },
    {
      icon: <Pen className="w-6 h-6" />,
      title: "Logo Design",
      description: "Distinctive logos that become the cornerstone of your brand recognition."
    },
    {
      icon: <Monitor className="w-6 h-6" />,
      title: "Web Design",
      description: "Stunning websites that combine aesthetics with seamless functionality."
    },
    {
      icon: <Package className="w-6 h-6" />,
      title: "Packaging",
      description: "Eye-catching packaging designs that stand out on shelves and online."
    },
    {
      icon: <Camera className="w-6 h-6" />,
      title: "Art Direction",
      description: "Strategic creative direction for campaigns that capture attention."
    }
  ];

  const portfolioItems = [
    {
      id: 1,
      title: "Geometric Branding",
      category: "BRAND IDENTITY",
      image: "https://images.unsplash.com/photo-1558655146-d09347e92766?w=800&h=600&fit=crop"
    },
    {
      id: 2,
      title: "Modern Logo Design",
      category: "LOGO DESIGN",
      image: "https://images.unsplash.com/photo-1626785774573-4b799315345d?w=800&h=600&fit=crop"
    },
    {
      id: 3,
      title: "Brand Collateral",
      category: "PRINT DESIGN",
      image: "https://images.unsplash.com/photo-1634942537034-2531766767d1?w=800&h=600&fit=crop"
    },
    {
      id: 4,
      title: "Packaging Design",
      category: "PACKAGING",
      image: "https://images.unsplash.com/photo-1615486511484-92e172cc4fe0?w=800&h=600&fit=crop"
    }
  ];

  const testimonials = [
    {
      text: "Working with Color Stencil transformed our brand identity. Their creative approach and attention to detail exceeded our expectations.",
      author: "Sarah Johnson",
      position: "CEO, TechStart Inc."
    },
    {
      text: "The team delivered exceptional designs that perfectly captured our vision. Professional, creative, and results-driven.",
      author: "Michael Chen",
      position: "Marketing Director, InnovateCo"
    },
    {
      text: "Outstanding work! They took our ideas and turned them into stunning visual experiences that our customers love.",
      author: "Emily Rodriguez",
      position: "Founder, Creative Solutions"
    }
  ];

  const stats = [
    { number: "150+", label: "PROJECTS COMPLETED" },
    { number: "50+", label: "HAPPY CLIENTS" },
    { number: "12", label: "YEARS EXPERIENCE" },
    { number: "25", label: "DESIGN AWARDS" }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        alert('Message sent successfully!');
        setFormData({ name: '', email: '', message: '' });
      } else {
        alert('Failed to send message. Please try again.');
      }
    } catch (error) {
      alert('Error sending message. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const nextTestimonial = () => {
    setCurrentTestimonial((prev) => (prev + 1) % testimonials.length);
  };

  const prevTestimonial = () => {
    setCurrentTestimonial((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  return (
    <div className="bg-[#111111] text-white">
      {/* Hero Section */}
      <section id="home" className="min-h-screen flex items-center justify-center px-4 relative overflow-hidden pt-16">
        <div className="absolute top-32 left-10 w-24 h-24 border-2 border-[#EC7B2F] opacity-20 rotate-45"></div>

        <div className="text-center max-w-4xl">
          <p className="text-[#EC7B2F] text-xs tracking-[0.3em] mb-4 font-light">
            CREATIVE DESIGN STUDIO
          </p>

          <h1 className="text-5xl md:text-6xl font-light mb-4">
            WE CREATE<br />
            <span className="text-[#EC7B2F] font-normal">BOLD</span> BRANDS
          </h1>

          <p className="text-gray-400 text-base font-light mb-8 max-w-2xl mx-auto">
            Award-winning graphic design agency crafting memorable visual identities<br />
            that make brands stand out in a crowded world.
          </p>

          <div className="flex gap-3 justify-center">
            <a
              href="#services"
              className="bg-[#EC7B2F] px-6 py-2.5 text-sm rounded flex items-center gap-2 hover:bg-[#d66b25] transition-all duration-300 shadow-lg hover:shadow-xl font-light"
            >
              VIEW OUR WORK →
            </a>
            <a
              href="#contact"
              className="border border-white px-6 py-2.5 text-sm rounded hover:bg-white hover:text-black transition-all duration-300 font-light"
            >
              GET IN TOUCH
            </a>
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section id="services" className="py-16 px-4">
        <div className="max-w-6xl mx-auto">
          <p className="text-[#EC7B2F] text-xs tracking-[0.3em] mb-3 text-center font-light">
            WHAT WE DO
          </p>
          <h2 className="text-4xl font-light text-center mb-12">OUR SERVICES</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {services.map((service, index) => (
              <div
                key={index}
                className="bg-[#1a1a1a] p-6 rounded-lg border border-gray-800 transition-all duration-300 hover:border-[#EC7B2F] hover:shadow-xl hover:shadow-[#EC7B2F]/20 hover:-translate-y-1 group"
              >
                <div className="bg-[#2a2a2a] w-12 h-12 rounded flex items-center justify-center mb-4 text-[#EC7B2F] group-hover:bg-[#EC7B2F] group-hover:text-white transition-all duration-300">
                  {service.icon}
                </div>
                <h3 className="text-lg font-normal mb-2">{service.title}</h3>
                <p className="text-gray-400 text-sm font-light">{service.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Portfolio Section */}
      <section id="portfolio" className="py-16 px-4 bg-[#0d0d0d]">
        <div className="max-w-6xl mx-auto">
          <p className="text-[#EC7B2F] text-xs tracking-[0.3em] mb-3 text-center font-light">
            OUR WORK
          </p>
          <h2 className="text-4xl font-light text-center mb-12">PORTFOLIO</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {portfolioItems.map((item) => (
              <div
                key={item.id}
                className="relative group overflow-hidden rounded-lg cursor-pointer h-64"
                onClick={() => setSelectedImage(item)}
              >
                <img 
                  src={item.image} 
                  alt={item.title}
                  className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                />

                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent opacity-60 group-hover:opacity-0 transition-opacity duration-500"></div>

                <div className="absolute inset-0 bg-[#EC7B2F]/0 group-hover:bg-[#EC7B2F]/60 transition-all duration-500"></div>

                <div className="absolute bottom-4 left-4 opacity-100 group-hover:opacity-0 transition-opacity duration-300">
                  <p className="text-xs tracking-wider mb-1 text-white/80 font-light">
                    {item.category}
                  </p>
                  <h3 className="text-lg font-normal text-white">
                    {item.title}
                  </h3>
                </div>

                <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-500">
                  <div className="text-center">
                    <p className="text-xs tracking-widest mb-2 text-white/90 font-light">
                      {item.category}
                    </p>
                    <h3 className="text-xl font-normal text-white">
                      {item.title}
                    </h3>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Image Modal */}
        {selectedImage && (
          <div 
            className="fixed inset-0 bg-black/90 z-50 flex items-center justify-center p-4"
            onClick={() => setSelectedImage(null)}
          >
            <div className="relative max-w-4xl w-full">
              <img 
                src={selectedImage.image} 
                alt={selectedImage.title}
                className="w-full h-auto rounded-lg"
              />
              <button 
                className="absolute top-4 right-4 text-white bg-black/50 w-10 h-10 rounded-full hover:bg-[#EC7B2F] transition-colors"
                onClick={() => setSelectedImage(null)}
              >
                ✕
              </button>
            </div>
          </div>
        )}
      </section>

      {/* Testimonials Section */}
      <section className="py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <div className="flex items-center justify-center gap-4">
            <button 
              onClick={prevTestimonial}
              className="w-10 h-10 border border-gray-700 rounded flex items-center justify-center hover:border-[#EC7B2F] hover:text-[#EC7B2F] transition-all duration-300"
            >
              <ChevronLeft size={20} />
            </button>

            <div className="flex-1 bg-[#1a1a1a] p-8 rounded-lg border border-gray-800 text-center">
              <p className="text-gray-300 text-base font-light mb-6 italic">
                "{testimonials[currentTestimonial].text}"
              </p>
              <div>
                <p className="font-normal text-white">
                  {testimonials[currentTestimonial].author}
                </p>
                <p className="text-sm text-gray-400 font-light">
                  {testimonials[currentTestimonial].position}
                </p>
              </div>
            </div>

            <button 
              onClick={nextTestimonial}
              className="w-10 h-10 border border-gray-700 rounded flex items-center justify-center hover:border-[#EC7B2F] hover:text-[#EC7B2F] transition-all duration-300"
            >
              <ChevronRight size={20} />
            </button>
          </div>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="py-16 px-4">
        <div className="max-w-6xl mx-auto">
          <p className="text-[#EC7B2F] text-xs tracking-[0.3em] mb-3 font-light">
            ABOUT US
          </p>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <div>
              <h2 className="text-4xl font-light mb-6">
                WE ARE<br />
                <span className="text-[#EC7B2F] font-normal">BOLD</span>
              </h2>

              <p className="text-gray-400 text-sm font-light mb-4">
                Founded in 2012, BOLD is a creative design studio that believes in the power of 
                exceptional design to transform businesses. We're a team of passionate designers, 
                strategists, and dreamers who live and breathe creativity.
              </p>

              <p className="text-gray-400 text-sm font-light mb-4">
                Our approach combines strategic thinking with bold creativity. We don't just design—we 
                craft experiences that connect brands with their audiences in meaningful ways. Every 
                project is an opportunity to push boundaries and create something extraordinary.
              </p>

              <p className="text-gray-400 text-sm font-light">
                From startups to Fortune 500 companies, we've helped brands across industries find their 
                voice and stand out in competitive markets.
              </p>
            </div>

            <div className="grid grid-cols-2 gap-4">
              {stats.map((stat, index) => (
                <div
                  key={index}
                  className="bg-[#1a1a1a] p-6 rounded-lg border border-gray-800 hover:scale-105 hover:shadow-lg hover:shadow-[#EC7B2F]/20 transition-all duration-300"
                >
                  <div className="text-4xl font-light text-[#EC7B2F] mb-3">
                    {stat.number}
                  </div>
                  <div className="text-xs text-gray-400 tracking-wider font-light">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section id="contact" className="py-16 px-4">
        <div className="max-w-6xl mx-auto">
          <p className="text-[#EC7B2F] text-xs tracking-[0.3em] mb-3 text-center font-light">
            GET IN TOUCH
          </p>
          <h2 className="text-4xl font-light text-center mb-12">CONTACT US</h2>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            <div>
              <h3 className="text-2xl font-light mb-4">LET'S CREATE SOMETHING BOLD</h3>
              <p className="text-gray-400 text-sm font-light mb-8">
                Ready to transform your brand? We'd love to hear about your project. Drop 
                us a line and let's start a conversation.
              </p>

              <div className="space-y-4">
                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                    <Mail size={18} />
                  </div>
                  <div>
                    <div className="text-xs text-gray-400 mb-1 font-light">EMAIL</div>
                    <div className="text-sm font-light">hello@colorstencil.com</div>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                    <Phone size={18} />
                  </div>
                  <div>
                    <div className="text-xs text-gray-400 mb-1 font-light">PHONE</div>
                    <div className="text-sm font-light">+1 (555) 123-4567</div>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 bg-[#2a2a2a] rounded flex items-center justify-center text-[#EC7B2F]">
                    <MapPin size={18} />
                  </div>
                  <div>
                    <div className="text-xs text-gray-400 mb-1 font-light">LOCATION</div>
                    <div className="text-sm font-light">New York, NY</div>
                  </div>
                </div>
              </div>
            </div>

            <div>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-xs text-gray-400 mb-1.5 font-light">NAME</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    placeholder="Your name"
                    className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-3 py-2 text-sm focus:border-[#EC7B2F] focus:outline-none transition-colors font-light"
                  />
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1.5 font-light">EMAIL</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="your@email.com"
                    className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-3 py-2 text-sm focus:border-[#EC7B2F] focus:outline-none transition-colors font-light"
                  />
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1.5 font-light">MESSAGE</label>
                  <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    placeholder="Tell us about your project..."
                    rows="5"
                    className="w-full bg-[#1a1a1a] border border-gray-800 rounded px-3 py-2 text-sm focus:border-[#EC7B2F] focus:outline-none transition-colors resize-none font-light"
                  ></textarea>
                </div>

                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="w-full bg-[#EC7B2F] px-6 py-2.5 text-sm rounded flex items-center justify-center gap-2 hover:bg-[#d66b25] transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed font-light"
                >
                  {isSubmitting ? 'SENDING...' : 'SEND MESSAGE'} <Send size={16} />
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
